third_tier_dat <- 
  fib_ami_dat %>% 
  filter(str_detect(outcome, "Third-tier efficacy"))

third_tier_dat %>% 
  gt(groupname_col = "outcome") %>% 
  hpp_tab(vertical_divider = "study") %>% 
  tab_header("Studies included in third-tier efficacy analysis")



