w_obs %>% 
  filter(condition_general == "fibromyalgia") %>%
  group_by(outcome, study) %>% 
  mutate(arms_per_study = 1:n()) %>% 
  filter(max(arms_per_study) > 1) %>% 
  # select(-arms_per_study) %>% 
  ungroup()





