# look at the first 0 matches
tar_read(w_obs_scale_counts) %>% 
  filter(cat_n == 0,
         # fix in Covidence bansal 2009 add in 0-4 
         study != "bansal 2009",
         # change kayiran vas 0-10 
         study != "kayiran 2010",
         # change extractions
         !str_detect(covidence_desc, "catastrophising") 
         )


