cookbook
pkg site


tar_read(w_obs) %>%
  # select first 10 columns
  select(1:5) %>% 
  select(-covidence) %>% 
  # first 10 rows
  head(3) %>% 
  gt() %>% 
  hpp_tab(vertical_divider = "outcome")



